class BUGSParserTokens
{
public:
	/** Symbolic definitions of all the tokens that the 
parser
 will work with.
	 * \{
	 *
	 * Antlr will define EOF, but we can't use that as it it is too common in
	 * in C header files and that would be confusing. There is no way to filter this out at the moment
	 * so we just undef it here for now. That isn't the value we get back from C recognizers
	 * anyway. We are looking for ANTLR_TOKEN_EOF.
	 */
	enum Tokens 
	{
		EOF_TOKEN = BUGSParserImplTraits::CommonTokenType::TOKEN_EOF
		, ABSOPENBRACKET = 4 
		, BERNOULLIOPENBRACKET = 5 
		, BETAOPENBRACKET = 6 
		, CENSORBEGINWITHC = 7 
		, CENSORBEGINWITHI = 8 
		, CLOGLOGOPENBRACKET = 9 
		, CLOSEBRACE = 10 
		, CLOSEBRACKET = 11 
		, CLOSESQUAREBRACKET = 12 
		, COLON = 13 
		, COMMA = 14 
		, CONSTANTINT = 15 
		, CONSTANTVALUE = 16 
		, DIRICHLETOPENBRACKET = 17 
		, DIV = 18 
		, DOT = 19 
		, EQUALTO = 20 
		, FORSTART = 21 
		, IN = 22 
		, INVERSEOPENBRACKET = 23 
		, LEFTPOINTER = 24 
		, LOGITOPENBRACKET = 25 
		, LOGOPENBRACKET = 26 
		, MINUS = 27 
		, MODEL = 28 
		, MULT = 29 
		, MUTLTINOMIALOPENBRACKET = 30 
		, NODENAME = 31 
		, OPENBRACE = 32 
		, OPENBRACKET = 33 
		, OPENSQUAREBRACKET = 34 
		, PLUS = 35 
		, PROBITOPENBRACKET = 36 
		, TILDE = 37 
		, TRUNCATIONBEGIN = 38 
		, WHITESPACE = 39 
	};

};

